extern "C" void TIM3_IRQHandler(void)
{
	pwmTim3.InterruptHandler();
	channel1.InterruptHandler();
}

void Delay(const uint32_t delay)
{
	const uint32_t startTime = SystemTimer::Now();
	while(SystemTimer::Now() < startTime + delay)
	{
		//just wait
	}
}

